import React from 'react';
import nba from 'nba';
import {Profile} from './Profile'
import { DataViewContainer } from './DataViewContainer';
import { SearchBar } from './SearchBar';
import { DEFAULT_PLAYER_INFO } from '../constants';

window.nba = nba;

export class Main extends React.Component {
    state = {
        playerInfo: DEFAULT_PLAYER_INFO
    }
    componentDidMount() {
        this.loadPlayerInfo(this.state.playerInfo.playerName);
    }

    loadPlayerInfo = (playerName) => {
        /* const playerId = nba.findPlayer('Stephen Curry').playerId; */
        const { playerId } = nba.findPlayer(playerName);
        console.log('Player Info3:');
        nba.stats.playerInfo({PlayerID: playerId}).then(
            (info) => {
                console.log(info);
                const commonPlayerInfo = info.commonPlayerInfo[0];
                const playerHeadlineStats = info.playerHeadlineStats[0];
                const playerInfo = {
                    ...commonPlayerInfo,
                    ...playerHeadlineStats
                };
                this.setState({
                    playerInfo
                });
            }
        );
    }

    render() {
        return (
            <div className = "main">
                <SearchBar loadPlayerInfo={this.loadPlayerInfo}/>
                <div className="player">
                    <Profile playerInfo = {this.state.playerInfo}/>
                    <DataViewContainer playerId={this.state.playerInfo.playerId}/>
                </div>

            </div>
        )
    }
}
